from color import rgb_to_hsv

import HelperFunctions as helpfunc

# How do you add an image to this show?
#
# Run the HexFilter7 processing sketch on the .jpg image of your choice.
# See the documentation for HexFilter7 at the top of its file
#
# Copy the Processing text output - image name to final parentheses - into the list below
# All images, except the final will need an additional trailing comma (,)


class Pictures(object):
    def __init__(self, hexmodel):
        self.name = "Pictures"        
        self.hexes = hexmodel
        self.pict_dict = {}  # Dictionary of pictures. Key = name, value = coord+rgb's

    def next_frame(self):
        self.parse_images()
        
        for name, data in self.pict_dict.items():
            self.print_image(data)
            yield 10  # seconds to display an image
    
    def print_image(self, data):
        for (x, y, r, g, b) in data:
            for h in helpfunc.all_hexes():
                self.hexes.set_cell( (h, x, y), rgb_to_hsv( (r, g, b) ))

